import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import ContactChannels from '../components/ContactChannels';
import style from '../styles/Contact.module.scss';
import axios from 'axios';
import { motion } from 'framer-motion';

const Contact = props => {

  const page = props.page;
  const user = props.user[0];

  const formatPhone = () => {
    const phone = user.phone;
    console.log(phone.replace(/(\d{3})(\d{3})(\d{4})/, "$1-$2-$3"));
  }

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
    >
      <Navbar logo={user.logoHeader.url} />

      <section className={`${style.ax_presentation} ${style.ax_contact}`}>
        <div className={style.ax_container}>
          <div className={style.ax_presentation_photo}>
            <img src={page.image.url} alt={page.title} />
          </div>
          <div className={style.ax_presentation_text}>
            <h1>{page.title}</h1>
            <p>{page.description}</p>
          </div>
        </div>
      </section>
      <section className={style.ax_contact_contact}>
        <div className={style.ax_container}>
          <ContactChannels address={user.address} phone={formatPhone} email={user.email} />
        </div>
      </section>
      <Footer
        logo={user.logoFooter.url}
        email={user.email}
        address={user.address}
        phone={user.phone}
        facebook={user.facebook}
        instagram={user.instagram}
        twitter={user.twitter}
        linkedin={user.linkedin}
        youtube={user.youtube}
      />
    </motion.div>
  )
}


export const getStaticProps = async () => {

  const data = axios.get(`https://axiomapi.herokuapp.com/page-contact`).then(res => {
    return res.data;
  }).then(async page => {
    const { data } = await axios.get(`https://axiomapi.herokuapp.com/users?email_eq=janedoe@axiommortgage.ca`);
    const user = data;

    return {
      props: {
        page,
        user
      }
    }

  }).catch(error => {
    console.log(error)
  });

  return data;

}

export default Contact;
