import Head from 'next/head';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import Button from '../components/Button';
import style from '../styles/About.module.scss';
import axios from 'axios';
import { motion } from 'framer-motion';

const Home = (props) => {

  const page = props.page;
  const user = props.user[0];

  return (
    <motion.div
      initial={{ x: 500, opacity: 0 }}
      animate={{ x: 0, opacity: 1 }}
      exit={{ x: -500, opacity: 0 }}
    >
      <Navbar logo={user.logoHeader.url} />
      <figure className={style.ax_banner}>
        <div className={style.ax_container}>
          <div className={style.ax_banner_info}>
            <h2>{page.banner.title}</h2>
            <p>{page.banner.subtitle}</p>
            <Button sizing="large" color="highlight" isLink linkPath={user.applicationLink} label="Apply Now" blank />
          </div>
        </div>
      </figure>
      <section className={style.ax_presentation}>
        <div className={style.ax_container}>
          <div className={style.ax_presentation_text}>
            <h3>{page.presentation.title}</h3>
            <p>{page.presentation.description} That's the {user.brokerage} advantage!</p>
            <Button sizing="large" color="highlight" isLink linkPath={user.applicationLink} label="Apply Now" blank />
          </div>
          <div className={style.ax_presentation_photo}>
            <img src={user.photo.url} alt={user.firstname} />
            <h3>{user.firstname} {user.lastname}</h3>
            <p>{user.position}</p>
            <p>{user.license ? user.license : ''}</p>
          </div>
        </div>
      </section>
      <Footer
        logo={user.logoFooter.url}
        email={user.email}
        address={user.address}
        phone={user.phone}
        facebook={user.facebook}
        instagram={user.instagram}
        twitter={user.twitter}
        linkedin={user.linkedin}
        youtube={user.youtube}
      />
    </motion.div>
  )
}



export const getStaticProps = async () => {

  const data = axios.get(`https://axiomapi.herokuapp.com/page-home`).then(res => {
    return res.data;
  }).then(async page => {
    const { data } = await axios.get(`https://axiomapi.herokuapp.com/users?email_eq=janedoe@axiommortgage.ca`);
    const user = data;

    // console.log('Page: ', page)
    // console.log('User: ', user)

    return {
      props: {
        page,
        user
      }
    }

  }).catch(error => {
    console.log(error)
  });

  return data;

}

export default Home;
