import Header from './Header'
import Footer from './Footer'
const Layout = props => {
  const page = props.page;
  const user = props.user[0];

  return (
    <>
      <Header />
      <main>
        {props.children}
      </main>
      <Footer
        logo={user.logoFooter}
        email={user.email}
        address={user.address}
        facebook={user.facebook}
        instagram={user.instagram}
        twitter={user.twitter}
        linkedin={user.linkedin}
        youtube={user.youtube}
      />
    </>

  )
}

export const getStaticProps = async () => {

  const data = axios.get(`https://axiomapi.herokuapp.com/page-home`).then(res => {
    return res.data;
  }).then(async page => {
    const { data } = await axios.get(`https://axiomapi.herokuapp.com/users?email_eq=janedoe@axiommortgage.ca`);
    const user = data;

    return {
      props: {
        page,
        user
      }
    }
  }).catch(error => {
    console.log(error)
  });
  return data;
}

export default Layout;
