import Head from 'next/head'
import Image from 'next/image'
import Content from '../content/pages.js';
// import Banner from '../public/assets/banner-home.jpg';
import Button from '../components/Button/Button';
import { CarouselProvider, Slider, Slide, ButtonBack, ButtonNext } from 'pure-react-carousel';
import 'pure-react-carousel/dist/react-carousel.es.css';
import '../styles/Home.module.scss';

const HomeContent = Content.pages.home;

if (typeof window !== 'undefined') {

  const responsiveCarousel = () => {
    let windowWidth = window.innerWidth;
    if (windowWidth >= 0 && windowWidth <= 767) {
      return 1;
    } else if (windowWidth >= 768 && windowWidth <= 1199) {
      return 2;
    } else {
      return 3;
    }
  }
}

const Home = () => {
  return (
    <>
      <figure className="ax-banner" style={{ backgroundImage: 'url("/assets/banner-home.jpg")' }}>
      </figure>
      <section className="ax-text-section">
        <div className="ax-container">
          <h2>{HomeContent.textSection.title}</h2>
          <p>{HomeContent.textSection.text}</p>
        </div>
      </section>
      <section className="ax-specialties ax-container">
        <h2>{HomeContent.specialtiesSection.title}</h2>
        {() => {
          if (typeof window !== 'undefined') {
            return (
              <CarouselProvider
                className="ax-carousel"
                naturalSlideWidth={100}
                naturalSlideHeight={125}
                visibleSlides={responsiveCarousel()}
                totalSlides={9}
                infinite={true}
                isPlaying={true}
                isIntrinsicHeight={true}
              >
                <Slider>
                  {HomeContent.specialtiesSection.specialties.map((item, index) => {
                    return (
                      <Slide index={index}>
                        <img src={item.img} alt={item.title} title={item.title} />
                        <h3>{item.title}</h3>
                        <p>{item.text}</p>
                        <Button sizing="medium" color="highlight" isCentered isLink linkPath="/#" label="Learn More" />
                      </Slide>
                    )
                  })}

                </Slider>
              </CarouselProvider>
            )
          }
        }}
      </section>
    </>
  )
}

export default Home;
