import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import CardWithIcon from '../components/CardWithIcon';
import { motion } from 'framer-motion';
import style from '../styles/MortgageProcess.module.scss';
import axios from 'axios';


const MortgageProcess = props => {
  const page = props.page;
  const user = props.user[0];

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
    >
      <Navbar logo={user.logoHeader.url} />
      <section className={style.ax_mortgage_process}>
        <div className={style.ax_container}>
          {page.steps.map((step, index) => {
            return (
              <CardWithIcon title={step.title} description={step.description} />
            )
          })
          }
        </div>
      </section>
      <Footer
        logo={user.logoFooter.url}
        email={user.email}
        address={user.address}
        phone={user.phone}
        facebook={user.facebook}
        instagram={user.instagram}
        twitter={user.twitter}
        linkedin={user.linkedin}
        youtube={user.youtube}
      />
    </motion.div>

  )
}

export const getStaticProps = async () => {

  const data = axios.get(`https://axiomapi.herokuapp.com/page-mortgage-process`).then(res => {
    return res.data;
  }).then(async page => {
    const { data } = await axios.get(`https://axiomapi.herokuapp.com/users?email_eq=janedoe@axiommortgage.ca`);
    const user = data;

    return {
      props: {
        page,
        user
      }
    }

  }).catch(error => {
    console.log(error)
  });

  return data;

}

export default MortgageProcess;
