import style from '../styles/CardWithIcon.module.scss';
import Button from './Button';

const CardWithIcon = props => {
  return (
    <div className={`${style.ax_card_with_icon} ${props.iconLeft ? style.ax_card_icon_left : ''}`}>
      <div className={style.ax_card_icon}>
        <img src={props.icon} />
      </div>
      <h3>{props.title}</h3>
      <p>{props.description}</p>
      {props.hasButton ? <Button sizing="small" color="highlight" isLink linkPath={`mailto:${props.email}`} label="Send me a message" blank /> : ''}
    </div>
  )
}

export default CardWithIcon;
