import { useState } from 'react';
import Router from 'next/router'
import Link from "next/link";
import style from '../styles/Navbar.module.scss';
import Head from 'next/head';
import NProgress from 'nprogress';

export const customLoader = ()=>{
  return(
    <div>
      <div className={`${style.ax_loading_bar} bar`} role="bar">
        <div className="peg">
        </div>
      </div>
      <div className={`${style.ax_loading_spinner} spinner`} role="spinner">
        <div className={`${style.ax_loading_spinner_icon} spinner-icon`}>
        </div>
      </div>
    </div>
  )
}

console.log(toString(customLoader()));

// NProgress.configure({
//   template: customLoader
// });

Router.events.on('routeChangeStart', (url) => {
  console.log(`Loading: ${url}`);
  NProgress.start();
})
Router.events.on('routeChangeComplete', () => NProgress.done());
Router.events.on('routeChangeError', () => NProgress.done());


const Navbar = (props) => {
  const [megaMenu, setMegaMenu] = useState(false);
  const [mobileMenu, setMobileMenu] = useState(false);
  const [mobileSubmenu, setMobileSubmenu] = useState(false);

  const handleMegaMenu = () => {
    let isOpen = !megaMenu;
    setMegaMenu(isOpen);
  }

  const handleMobileMenu = () => {
    let isMobileOpen = !mobileMenu;
    setMobileMenu(isMobileOpen);
  }

  const handleMobileSubmenu = () => {
    let isMobileSubmenuOpen = !mobileSubmenu;
    setMobileSubmenu(isMobileSubmenuOpen);
  }

  return (
    <>
      <Head>
        <title>{props.siteTitle}</title>
      </Head>
      <header className={style.ax_header}>
        <section className={style.ax_topbar}>

        </section>
        <section className={style.ax_navbar}>
          <div className={style.ax_logo}>
            <img src={props.logo} layout="fill" />
          </div>
          <nav className={style.ax_menu}>
            <Link href="/" >Home</Link>
            <Link href="/about">About</Link>
            <Link href="/mortgage-process" >Mortgage Process</Link>
            <Link href="/calculator" >Calculator</Link>

            <button className={style.ax_has_submenu} onClick={handleMegaMenu}>Learn

            </button>
            <Link href="/faq" >FAQ</Link>
            <Link href="/contact" >Contact</Link>
          </nav>

          <div>            
          </div>          
        </section>
      </header>

      <div className={`${style.ax_btn_menu_mob} ${mobileMenu ? style.ax_btn_menu_mob_open : ''} ${mobileSubmenu ? style.ax_hide_btn_menu_mob : ''}`} onClick={handleMobileMenu}>
        <span></span>
        <span></span>
        <span></span>
      </div>

      <div className={`${style.ax_mobile_menu} ${mobileMenu ? style.ax_menu_mob_open : ''} ${mobileSubmenu ? style.ax_submenu_mob_open : ''}`}>
        <nav>
          <Link href="/" >Home</Link>
          <Link href="/about">About</Link>
          <Link href="/mortgage-process" >Mortgage Process</Link>
          <Link href="/calculator" >Calculator</Link> 
          <div href="#" className={style.ax_has_submenu_mob} onClick={handleMobileSubmenu}>Learn
            <div className={`${style.ax_submenu_mob}`}>
              <button onClick={handleMobileSubmenu}>Back</button>
              <h3>Learning Centre</h3>
                <Link href="/learning-centre" >Learning Centre</Link>
                <Link href="/about-learning-centre" >About Learning Centre</Link>
                <Link href="/the-mortgage-process" >The Mortgage Process</Link>
                <Link href="/why-use-a-mortgage-broker" >Why Use a Mortgage Broker</Link>
                <Link href="/mortgage-101" >Mortgage 101</Link>
                <Link href="/glossary" >Glossary</Link>
                <h3>Mortgage Solutions</h3>
                <Link href="/mortgage-solutions" >Mortgage Solutions</Link>
                <Link href="/finding-the-right-solutions" >Finding the Right Solutions</Link>
                <Link href="/mortgage-renewals" >Mortgage Renewals</Link>
                <Link href="/mortgage-refinancing" >Mortgage Refinancing</Link>
                <Link href="/home-equity-loans" >Home Equity Loans</Link>
                <Link href="/self-employed-loans" >Self Employed Loans</Link>
                <Link href="/purchase-plus-improvements" >Purchase Plus Improvements</Link>
                <Link href="/debt-consolidation" >Debt Consolidation</Link>
            </div>
          </div>                           
          <Link href="/faq" >FAQ</Link>
          <Link href="/contact" >Contact</Link>
        </nav>
      </div>


      <div className={`${style.ax_megamenu} ${megaMenu ? style.ax_megamenu_open : style.ax_megamenu_close} `}>
        <nav>
          <h3>Learning Centre</h3>
          <Link href="/learning-centre" >Learning Centre</Link>
          <Link href="/about-learning-centre" >About Learning Centre</Link>
          <Link href="/the-mortgage-process" >The Mortgage Process</Link>
          <Link href="/why-use-a-mortgage-broker" >Why Use a Mortgage Broker</Link>
          <Link href="/mortgage-101" >Mortgage 101</Link>
          <Link href="/glossary" >Glossary</Link>
        </nav>
        <nav>
          <h3>Mortgage Solutions</h3>
          <Link href="/mortgage-solutions" >Mortgage Solutions</Link>
          <Link href="/finding-the-right-solutions" >Finding the Right Solutions</Link>
          <Link href="/mortgage-renewals" >Mortgage Renewals</Link>
          <Link href="/mortgage-refinancing" >Mortgage Refinancing</Link>
          <Link href="/home-equity-loans" >Home Equity Loans</Link>
          <Link href="/self-employed-loans" >Self Employed Loans</Link>
          <Link href="/purchase-plus-improvements" >Purchase Plus Improvements</Link>
          <Link href="/debt-consolidation" >Debt Consolidation</Link>
        </nav>
        <button className={style.ax_close_megamenu} onClick={handleMegaMenu}>Close</button>
      </div>
    </>
  )
}

export default Navbar;
