import { useRouter } from 'next/router'
import Navbar from '../../components/Navbar';
import Footer from '../../components/Footer';
import Button from '../../components/Button';
import CardWithIcon from '../../components/CardWithIcon';
import style from '../../styles/Posts.module.scss';
import axios from 'axios';
import { motion } from 'framer-motion';

const Post = props => {

  const router = useRouter()
  const { slug } = router.query

  const post = props.post;
  const user = props.user[0];

  console.log(props)

  const formatPhone = () => {
    const phone = user.phone;
    return `${phone.slice(0, 3)}-${phone.slice(3, 6)}-${phone.slice(6, 10)}`;
  }

  const formatedPhone = formatPhone();

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
    >
      <Navbar logo={user.logoHeader.url} email={user.email} phone={formatedPhone} />

      <section className={style.ax_post}>
        <div className={style.ax_container}>
          <h1>{post.pagetitle}</h1>

          <article className={style.ax_post_content}>
            {post.content}
            <Button sizing="large" color="highlight" isLink linkPath={user.applicationLink} label="Apply Now" blank />
          </article>
          <aside className={style.ax_sidebar}>

          </aside>
        </div>
      </section>

      <Footer
        logo={user.logoFooter.url}
        email={user.email}
        address={user.address}
        phone={user.phone}
        facebook={user.facebook}
        instagram={user.instagram}
        twitter={user.twitter}
        linkedin={user.linkedin}
        youtube={user.youtube}
      />
    </motion.div>
  )
}

export async function getStaticPaths() {
  const res = await fetch('https://axiomapi.herokuapp.com/site-posts')
  const posts = await res.json()

  const paths = posts.map((post) => `/posts/${post.slug}`)
  return { paths, fallback: false }
}

export const getStaticProps = async ({ params }) => {
  const data = axios.get(`https://axiomapi.herokuapp.com/site-posts/${params.id}`).then(res => {
    return res.data;
  }).then(async post => {
    const { data } = await axios.get(`https://axiomapi.herokuapp.com/users?email_eq=janedoe@axiommortgage.ca`);
    const user = data;

    return {
      props: {
        post,
        user
      }
    }

  }).catch(error => {
    console.log(error)
  });

  return data;
}




export default Post;
