import { CarouselProvider, Slider, Slide, ButtonBack, ButtonNext } from 'pure-react-carousel';
import Image from 'next/image'
import Button from '../components/Button'; import 'pure-react-carousel/dist/react-carousel.es.css';
import style from '../styles/Carousel.module.scss';

const CarouselSpecialties = (props) => {
  const carousel = props.features;

  return (
    <div className={style.ax_carousel}>
      <CarouselProvider
        className={style.ax_carousel_dkt}
        naturalSlideWidth={100}
        naturalSlideHeight={125}
        visibleSlides={3}
        totalSlides={9}
        infinite={true}
        isPlaying={true}
        isIntrinsicHeight={true}
      >
        <Slider>
          {props.features.map((item, index) => {
            return (
              <Slide index={index} key={index}>
                <img src={item.img} alt={item.title} title={item.title} />
                <h3>{item.title}</h3>
                <p>{item.description}</p>
                <Button sizing="medium" color="highlight" isCentered isLink linkPath={item.url} label="Learn More" />
              </Slide>
            )
          })}

        </Slider>
      </CarouselProvider>


      <CarouselProvider
        className={style.ax_carousel_tablet}
        naturalSlideWidth={100}
        naturalSlideHeight={125}
        visibleSlides={2}
        totalSlides={9}
        infinite={true}
        isPlaying={true}
        isIntrinsicHeight={true}
      >
        <Slider>
          {HomeContent.specialtiesSection.specialties.map((item, index) => {
            return (
              <Slide index={index} key={index}>
                <img src={item.img} alt={item.title} title={item.title} />
                <h3>{item.title}</h3>
                <p>{item.text}</p>
                <Button sizing="medium" color="highlight" isCentered isLink linkPath="/#" label="Learn More" />
              </Slide>
            )
          })}

        </Slider>
      </CarouselProvider>

      <CarouselProvider
        className={style.ax_carousel_mob}
        naturalSlideWidth={100}
        naturalSlideHeight={125}
        visibleSlides={1}
        totalSlides={9}
        infinite={true}
        isPlaying={true}
        isIntrinsicHeight={true}
      >
        <Slider>
          {HomeContent.specialtiesSection.specialties.map((item, index) => {
            return (
              <Slide index={index} key={index}>
                <img src={item.img} alt={item.title} title={item.title} />
                <h3>{item.title}</h3>
                <p>{item.text}</p>
                <Button sizing="medium" color="highlight" isCentered isLink linkPath="/#" label="Learn More" />
              </Slide>
            )
          })}

        </Slider>
      </CarouselProvider>
    </div>


  )
}

export const getStaticProps = async () => {

  const data = axios.get(`https://axiomapi.herokuapp.com/page-home`).then(res => {
    let page = res.data;

    return {
      props: {
        page
      }
    }

  }).catch(error => {
    console.log(error)
  });

  return data;

}

export default CarouselSpecialties;
