import currentUser from '../utils/currentUser';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import Button from '../components/Button';
import CardWithIcon from '../components/CardWithIcon';
import style from '../styles/Calculators.module.scss';
import axios from 'axios';
import { motion } from 'framer-motion';

const Home = props => {

  const page = props.page;
  const user = props.user[0];

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
    >
      <Navbar logo={user.logoHeader.url} logo2={user.hasLogo2 ? user.logoHeader2.url : ''} email={user.email} phone={user.phone} showWhatsAppButton={user.whatsapp} />

      <section className={style.ax_calculators}>
        <div className={style.ax_container}>
          <div className={style.ax_calculators_list}>
            <CardWithIcon icon="./assets/calculator.svg" title="Mortgage Loan" description="description here" hasButton isLink linkUrl="/calculators/mortgage-loan" buttonLabel="Go To Calculator" />
            <CardWithIcon icon="./assets/compare.svg" title="Compare Mortgage" description="description here" hasButton isLink linkUrl="/calculators/compare-mortgage" buttonLabel="Go To Calculator" />
            <CardWithIcon icon="./assets/qualifier.svg" title="Mortgage Qualifier" description="description here" hasButton isLink linkUrl="/calculators/mortgage-qualifier" buttonLabel="Go To Calculator" />
            <CardWithIcon icon="./assets/payoff.svg" title="Mortgage Payoff" description="description here" hasButton isLink linkUrl="/calculators/mortgage-payoff" buttonLabel="Go To Calculator" />
            <CardWithIcon icon="./assets/refinance.svg" title="Mortgage Refinance" description="description here" hasButton isLink linkUrl="/calculators/mortgage-refinance" buttonLabel="Go To Calculator" />
          </div>
        </div>
      </section>

      <Footer
        logo={user.logoFooter.url}
        email={user.email}
        address={user.address}
        phone={user.phone}
        facebook={user.facebook}
        instagram={user.instagram}
        twitter={user.twitter}
        linkedin={user.linkedin}
        youtube={user.youtube}
      />
    </motion.div>
  )
}


export const getStaticProps = async () => {

  const data = axios.get(`https://axiomapi.herokuapp.com/page-about`).then(res => {
    return res.data;
  }).then(async page => {
    const { data } = await axios.get(`https://axiomapi.herokuapp.com/users?email_eq=${currentUser.email}`);
    const user = data;

    return {
      props: {
        page,
        user
      }
    }

  }).catch(error => {
    console.log(error)
  });

  return data;

}

export default Home;
