import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import CardWithIcon from '../components/CardWithIcon';
import Button from '../components/Button';
import { motion } from 'framer-motion';
import style from '../styles/MortgageProcess.module.scss';
import axios from 'axios';


const MortgageProcess = props => {

  const page = props.page;
  const user = props.user[0];

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
    >
      <Navbar logo={user.logoHeader.url} email={user.email} phone={user.phone} showWhatsAppButton={user.whatsapp} />
      <section className={style.ax_mortgage_process}>
        <div className={style.ax_container}>
          <h1>{page.title}</h1>
          <h3>{page.subtitle}</h3>
          <div className={style.ax_process_list}>
            {page.steps.map((step, index) => {
              return (
                <CardWithIcon key={index} title={step.title} description={step.description} icon={step.image.url} />
              )
            })
            }
          </div>
          <div className={style.ax_process_cta}>
            <h3>{page.ctaTitle}</h3>
            <p>{page.ctaDescription}</p>

            <Button sizing="large" color="highlight" isLink linkPath={user.applicationLink} label="Apply Now" blank />

          </div>
        </div>
      </section>
      <Footer
        logo={user.logoFooter.url}
        email={user.email}
        address={user.address}
        phone={user.phone}
        facebook={user.facebook}
        instagram={user.instagram}
        twitter={user.twitter}
        linkedin={user.linkedin}
        youtube={user.youtube}
      />
    </motion.div>

  )
}

export const getStaticProps = async () => {

  const data = axios.get(`https://axiomapi.herokuapp.com/page-mortgage-process`).then(res => {
    return res.data;
  }).then(async page => {
    const { data } = await axios.get(`https://axiomapi.herokuapp.com/users?email_eq=janedoe@axiommortgage.ca`);
    const user = data;

    return {
      props: {
        page,
        user
      }
    }

  }).catch(error => {
    console.log(error)
  });

  return data;

}

export default MortgageProcess;
