import '../../styles/Button.module.scss';

const Button = (props) => {

  const colorClass = () => {
    return props.color === 'highlight' ? 'ax-btn-highlight' : 'ax-btn-base';
  }

  const centeredClass = () => {
    return props.isCentered ? 'ax-btn-centered' : '';
  }

  const wideClass = () => {
    return props.isWide ? 'ax-btn-wide' : '';
  }

  const sizeClass = () => {
    switch (props.sizing) {
      case 'small': return 'ax-btn-small';
      case 'medium': return 'ax-btn-medium';
      case 'large': return 'ax-btn-large';
      case 'xlarge': return 'ax-btn-xlarge';
      default: return 'ax-btn-medium';
    }
  }


  if (props.isLink) {
    return (
      <a href={props.linkPath} className={`ax-btn ${sizeClass()} ${colorClass()} ${centeredClass()} ${wideClass()}}`}>{props.label}</a>
    )
  }
  return <button className={`ax-btn ${sizeClass()} ${colorClass()} ${centeredClass()} ${wideClass()}}`}>{props.label}</button>
}

export default Button;
