import Head from 'next/head'
import Image from 'next/image'
import Content from '../content/pages.js';
import Button from '../components/Button';
import CarouselSpecialties from '../components/CarouselSpecialties';
import toParagraph from '../utils/paragraphFormatter';
import remark, { process } from 'remark';
import markdown from 'remark-parse';
import html from 'remark-html'
import style from '../styles/Home.module.scss';
import axios from 'axios';

const Home = (props) => {

  const page = props.page;
  const user = props.user[0];

  return (
    <>
      <figure className={style.ax_banner}>
        <h2>{page.banner.title}</h2>
      </figure>
      <section className={style.ax_text_section}>
        <div className={style.ax_container}>
          <h2>Hi. We're so glad you stopped by!</h2>
          <p>{page.intro.description}</p>
        </div>
      </section>
      <section className={`${style.ax_specialties} ${style.ax_container}`}>
        <h2>Our Specialties</h2>
        <div className={style.ax_container}>
          <CarouselSpecialties carouselData={page.features} />
        </div>
      </section>
      <section className={style.ax_calculator}>
        <div className={style.ax_container}>
          <div className={style.ax_calculator_cta}>
            <div>
              <h3>{page.calculator.title}</h3>
              <Button sizing="large" color="highlight" isLink linkPath={`/${page.calculator.url}`} label="Go To Calculator" />
            </div>
          </div>
        </div>
      </section>
      <section className={style.ax_presentation}>
        <div className={style.ax_container}>
          <div className={style.ax_presentation_text}>
            <h3>{page.presentation.title}</h3>
            <p>{page.presentation.description} That's the {user.brokerage} advantage!</p>
            <Button sizing="large" color="highlight" isLink linkPath={user.applicationlink} label="Apply Now" />
          </div>
          <div className={style.ax_presentation_photo}>
            <img src={user.photo.url} alt={user.firstname} />
            <h3>{user.firstname} {user.lastname}</h3>
            <p>{user.position}</p>
            <p>{user.license ? user.license : ''}</p>
          </div>
        </div>
      </section>
    </>
  )
}



export const getStaticProps = async () => {

  const data = axios.get(`https://axiomapi.herokuapp.com/page-home`).then(res => {
    return res.data;
  }).then(async page => {
    const { data } = await axios.get(`https://axiomapi.herokuapp.com/users?email_eq=janedoe@axiommortgage.ca`);
    const user = data;

    console.log('Page: ', page)
    console.log('User: ', user)

    return {
      props: {
        page,
        user
      }
    }

  }).catch(error => {
    console.log(error)
  });

  return data;

}

export default Home;
