import { CarouselProvider, Slider, Slide, ButtonBack, ButtonNext } from 'pure-react-carousel';
import Image from 'next/image'
import Content from '../content/pages.js';
import Button from '../components/Button'; import 'pure-react-carousel/dist/react-carousel.es.css';
import style from '../styles/Carousel.module.scss';

const CarouselSpecialties = () => {
  const HomeContent = Content.pages.home;

  return (
    <div className={style.ax_carousel}>
      <CarouselProvider
        className={style.ax_carousel_dkt}
        naturalSlideWidth={100}
        naturalSlideHeight={125}
        visibleSlides={3}
        totalSlides={9}
        infinite={true}
        isPlaying={true}
        isIntrinsicHeight={true}
      >
        <Slider>
          {HomeContent.specialtiesSection.specialties.map((item, index) => {
            return (
              <Slide index={index}>
                <Image src={item.img} alt={item.title} title={item.title} />
                <h3>{item.title}</h3>
                <p>{item.text}</p>
                <Button sizing="medium" color="highlight" isCentered isLink linkPath="/#" label="Learn More" />
              </Slide>
            )
          })}

        </Slider>
      </CarouselProvider>


      <CarouselProvider
        className={style.ax_carousel_tablet}
        naturalSlideWidth={100}
        naturalSlideHeight={125}
        visibleSlides={2}
        totalSlides={9}
        infinite={true}
        isPlaying={true}
        isIntrinsicHeight={true}
      >
        <Slider>
          {HomeContent.specialtiesSection.specialties.map((item, index) => {
            return (
              <Slide index={index}>
                <Image src={item.img} alt={item.title} title={item.title} />
                <h3>{item.title}</h3>
                <p>{item.text}</p>
                <Button sizing="medium" color="highlight" isCentered isLink linkPath="/#" label="Learn More" />
              </Slide>
            )
          })}

        </Slider>
      </CarouselProvider>

      <CarouselProvider
        className={style.ax_carousel_mob}
        naturalSlideWidth={100}
        naturalSlideHeight={125}
        visibleSlides={1}
        totalSlides={9}
        infinite={true}
        isPlaying={true}
        isIntrinsicHeight={true}
      >
        <Slider>
          {HomeContent.specialtiesSection.specialties.map((item, index) => {
            return (
              <Slide index={index}>
                <Image src={item.img} alt={item.title} title={item.title} />
                <h3>{item.title}</h3>
                <p>{item.text}</p>
                <Button sizing="medium" color="highlight" isCentered isLink linkPath="/#" label="Learn More" />
              </Slide>
            )
          })}

        </Slider>
      </CarouselProvider>
    </div>


  )
}

export default CarouselSpecialties;
