import { CarouselProvider, Slider, Slide, ButtonBack, ButtonNext } from 'pure-react-carousel';
import Image from 'next/image'
import Button from '../components/Button'; import 'pure-react-carousel/dist/react-carousel.es.css';
import style from '../styles/Carousel.module.scss';

const CarouselSpecialties = (props) => {
  let carousel = [
    {
      _id: '5fd3824397a95ad9571f8672',
      title: 'Purchase',
      description: 'Ready to buy? Let’s get you Pre-Approved! Lock in your rate and shop with confidence!',
      url: '/the-mortgage-process',
      __v: 0,
      image: [Object],
      id: '5fd3824397a95ad9571f8672'
    },
    {
      _id: '5fd3c52f0d7a8df4f68b3e54',
      title: 'First Time Home Buyer(s)',
      description: 'Preparing to purchase your first home? Stepping into the world of home-ownership for the first-time is an exciting and rewarding time. It can also be overwhelming and confusing; there are many things to consider. The great news is that we’ve helped thousands of home-owners with their mortgage needs. We’re here to walk you through the entire process! We are mortgage pro’s!',
      url: '/the-mortgage-process',
      __v: 0,
      image: [Object],
      id: '5fd3c52f0d7a8df4f68b3e54'
    },
    {
      _id: '5fd3c52f0d7a8df4f68b3e55',
      title: 'Mortgage Renewals',
      description: 'Don’t simply ‘sign back’ your mortgage renewal offer from your existing lender. Don’t assume you will be offered the best available rate (this is often not the case). Let us take a look at your renewal offer to determine if it’s worth staying with your existing lender or if it’s time to make a switch. Ensure you are in the best mortgage product at a great rate!',
      url: '/mortgage-renewal',
      __v: 0,
      image: [Object],
      id: '5fd3c52f0d7a8df4f68b3e55'
    },
    {
      _id: '5fd3c52f0d7a8df4f68b3e56',
      title: 'Mortgage Refinancing',
      description: 'Looking to get out of a high rate mortgage? Need access to your available equity? A mortgage refinance is an opportunity to reduce your rate and/or unlock equity. Whether it’s for debt-consolidation, a sought after renovation, or an investment opportunity; we’ll get you the money you need, at the best available rate!',
      url: '/mortgage-refinance',
      __v: 0,
      image: [Object],
      id: '5fd3c52f0d7a8df4f68b3e56'
    },
    {
      _id: '5fd3c52f0d7a8df4f68b3e57',
      title: 'Credit Improvement',
      description: 'Are you coming back from a life event that affected your credit? Looking to improve your current situation? We have mortgage lenders who will consider bruised credit (for your right-now solution) and/or work with you to start improving your credit for the best mortgage rate in the future!',
      url: '/credit-basics',
      __v: 0,
      image: [Object],
      id: '5fd3c52f0d7a8df4f68b3e57'
    },
    {
      _id: '5fd3c52f0d7a8df4f68b3e58',
      title: 'Reverse Mortgages',
      description: 'Retiring shouldn’t mean having to sell your home or adding additional debt to make staying in your home possible. We have access to reverse mortgage options which allow Canadians a safe and secure way to access up to 55% of the value of their home. If you are 55+ this may be your path to freedom and the opportunity to enjoy your best life! Contact us for more information on how to put this to work for you and/or a loved one!',
      url: '/reverse-mortgage',
      __v: 0,
      image: [Object],
      id: '5fd3c52f0d7a8df4f68b3e58'
    },
    {
      _id: '5fd3c52f0d7a8df4f68b3e59',
      title: 'Spousal Buyout',
      url: '/spousal-buyout',
      description: 'Do you need to dissolve ties with your ex (or soon to be) ex-spouse? Are one of you looking to stay in the “matrimonial home”? The Spousal Buyout is available for those who are in the process of a legal separation where public/assets/property are being divided. This mortgage allows for one of the  jointly titled homeowners to buy the other out (includes common-law partners); allowing you to move forward with life. Separation is hard enough, let us help alleviate some of the stress.',
      __v: 0,
      image: [Object],
      id: '5fd3c52f0d7a8df4f68b3e59'
    },
    {
      _id: '5fd3c52f0d7a8df4f68b3e5a',
      title: 'New To Canada',
      url: '/new-to-canada',
      description: 'Have you immigrated or relocated to Canada within the last 5 years? Do you have a valid work permit or permanent residency? If you have answered yes to both these questions you can own a home for as little as 5% down payment. Contact us for more details – we’d love to help make your dreams of owning a home in Canada a reality!',
      __v: 0,
      image: [Object],
      id: '5fd3c52f0d7a8df4f68b3e5a'
    },
    {
      _id: '5fd3c52f0d7a8df4f68b3e5b',
      title: 'Construction Mortgage',
      description: 'Are you looking to undertake the construction of a custom-built home, investment or vacation property? Construction loans are available. While they are common, they are not your usual mortgage. Put our expertise to work for you!',
      url: '/construction-mortgage',
      __v: 0,
      image: [Object],
      id: '5fd3c52f0d7a8df4f68b3e5b'
    }
  ];

  console.log('from carousel', carousel);

  return (
    <div className={style.ax_carousel} >
      <CarouselProvider
        className={style.ax_carousel_dkt}
        naturalSlideWidth={100}
        naturalSlideHeight={125}
        visibleSlides={3}
        totalSlides={9}
        infinite={true}
        isPlaying={true}
        isIntrinsicHeight={true}
      >
        <Slider>
          {carousel.map((item, index) => {
            return (
              <Slide index={index} key={index}>
                <img src={item.img} alt={item.title} title={item.title} />
                <h3>{item.title}</h3>
                <p>{item.description}</p>
                <Button sizing="medium" color="highlight" isCentered isLink linkPath={item.url} label="Learn More" />
              </Slide>
            )
          })}

        </Slider>
      </CarouselProvider>


      <CarouselProvider
        className={style.ax_carousel_tablet}
        naturalSlideWidth={100}
        naturalSlideHeight={125}
        visibleSlides={2}
        totalSlides={9}
        infinite={true}
        isPlaying={true}
        isIntrinsicHeight={true}
      >
        <Slider>
          {carousel.map((item, index) => {
            return (
              <Slide index={index} key={index}>
                <img src={item.img} alt={item.title} title={item.title} />
                <h3>{item.title}</h3>
                <p>{item.description}</p>
                <Button sizing="medium" color="highlight" isCentered isLink linkPath={item.url} label="Learn More" />
              </Slide>
            )
          })}

        </Slider>
      </CarouselProvider>

      <CarouselProvider
        className={style.ax_carousel_mob}
        naturalSlideWidth={100}
        naturalSlideHeight={125}
        visibleSlides={1}
        totalSlides={9}
        infinite={true}
        isPlaying={true}
        isIntrinsicHeight={true}
      >
        <Slider>
          {carousel.map((item, index) => {
            return (
              <Slide index={index} key={index}>
                <img src={item.img} alt={item.title} title={item.title} />
                <h3>{item.title}</h3>
                <p>{item.description}</p>
                <Button sizing="medium" color="highlight" isCentered isLink linkPath={item.url} label="Learn More" />
              </Slide>
            )
          })}

        </Slider>
      </CarouselProvider>
    </div>


  )
}

export default CarouselSpecialties;
