import Head from 'next/head'
import Image from 'next/image'
import Content from '../content/pages.js';
import Button from '../components/Button';
import CarouselSpecialties from '../components/CarouselSpecialties';
import toParagraph from '../utils/paragraphFormatter';
import remark from 'remark';
import markdown from 'remark-parse';
import html from 'remark-html'
import style from '../styles/Home.module.scss';

const HomeContent = Content.pages.home;

const resultHTML = remark().use(markdown).use(html).process();

const Home = () => {

  const presentationTextRaw = HomeContent.presentationSection.text;
  const presentationText = resultHTML(presentationTextRaw);

  return (
    <>
      <figure className={style.ax_banner}>
      </figure>
      <section className={style.ax_text_section}>
        <div className={style.ax_container}>
          <h2>{HomeContent.textSection.title}</h2>
          <p>{HomeContent.textSection.text}</p>
        </div>
      </section>
      <section className={`${style.ax_specialties} ${style.ax_container}`}>
        <h2>{HomeContent.specialtiesSection.title}</h2>
        <div className={style.ax_container}>
          <CarouselSpecialties />
        </div>
      </section>
      <section className={style.ax_calculator}>
        <div className={style.ax_container}>
          <div className={style.ax_calculator_cta}>
            <div>
              <h3>{HomeContent.calculatorSection.title}</h3>
              <Button sizing="large" color="highlight" isLink linkPath="/#" label="Go To Calculator" />
            </div>
          </div>
        </div>
      </section>
      <section className={style.ax_presentation}>
        <div className={style.ax_container}>
          <div>
            <h3>{HomeContent.presentationSection.title}</h3>
            <div>{presentationText}</div>
          </div>
        </div>
      </section>
    </>
  )
}

export default Home;
